						} else {
							item.setIcon(
								'http://maps.google.com/mapfiles/ms/icons/red-dot.png'
							);
						}
					});
				});
			}
		);
	};

	handleChangeGanivoIcon = (type: number = 1) => {
		if (type === 1) {
