import { Component, OnInit } from '@angular/core';
import { Loader } from '@googlemaps/js-api-loader';
let map: google.maps.Map;
const VietNamCor = { lat: 15.9031, lng: 105.8067 };
const flightPlanCoordinates = [
  { lat: 37.772, lng: -122.214 },
  { lat: 21.291, lng: -157.821 },
  { lat: -18.142, lng: 178.431 },
  { lat: -27.467, lng: 153.027 },
];

const triangleCoords = [
  { lat: 23.393396, lng: 102.14682 },
  { lat: 22.719568, lng: 105.776716 },
  { lat: 8.383496, lng: 104.938748 },
  { lat: 8.383496, lng: 102.14682 },
];
let loader = new Loader({
  apiKey: 'AIzaSyB7HYSRAc7_Tyo5kOt4xtkbjcYmHKTs0no',
  version: 'weekly',
});
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent implements OnInit {
  title = 'AngularDemo';

  ngOnInit(): void {
    this.demo();
  }

  demo() {
    loader.load().then(async () => {
      //* initial map element
      const { Map } = (await google.maps.importLibrary(
        'maps'
      )) as google.maps.MapsLibrary;
      map = new Map(document.getElementById('map') as HTMLElement, {
        center: VietNamCor,
        zoom: 8,
      });

      //* Create marker instance
      const marker = new google.maps.Marker({
        position: VietNamCor,
        map,
        title: 'Hello World!',
      });

      //* Create polyline instance
      const poly = new google.maps.Polyline({
        path: flightPlanCoordinates,
        geodesic: true,
        strokeColor: '#FF0000',
        strokeOpacity: 1.0,
        strokeWeight: 2,
      });

      //* Create the polygon instance
      const polygon = new google.maps.Polygon({
        paths: triangleCoords,
        strokeColor: '#FF0000',
        strokeOpacity: 0.8,
        strokeWeight: 2,
        fillColor: '#FF0000',
        fillOpacity: 0.35,
      });

      //* create circle instance.
      const circle = new google.maps.Circle({
        editable: true,
        strokeColor: '#FF0000',
        strokeOpacity: 0.8,
        strokeWeight: 2,
        fillColor: '#FF0000',
        fillOpacity: 0.35,
        map,
        center: { lat: 40.714, lng: -74.005 },
        radius: Math.sqrt(8405837) * 100,
      });

      //! Add click event listener to marker instance
      marker.addListener('click', () => {
        console.log('click event fired');
      });

      poly.setMap(map);
      polygon.setMap(map);
    });
  }
}
